import React from 'react'

const Footer = () => {
  const pages = [
    { page: "Home", href: '/' },
    { page: "Portfolio", href: "/portfolio" },
    { page: "Contact", href: "contact" }
  ]
  const socials = [
    { name: 'Github', href:""},
    { name: "Linkedin", href:""},
    { name: "Instagram", href:""}
  ]
  const contactInfo = [
    'bandiavi000@isd284.com',
    '310-362-7663'
  ]
  return (
    <div className='max-w-2xl mx-auto mt-32'>
      <div className='flex justify-between'>
        <div>
          <h1 className='text-black text-lg font-montserrat font-semibold mb-5'>Pages</h1>
          {
            pages.map((page) => (
              <a href={page.href}>
                <p className='text-slate-gray text-lg font-montserrat font-semibold mt-1'>{page.page}</p>
              </a>
            ))
          }
        </div>
        <div>
          <h1 className='text-black text-lg font-montserrat font-semibold mb-5'>Socials</h1>
          {
            socials.map((social) =>(
              <p className='text-slate-gray text-lg font-montserrat font-semibold mt-1'>{social.name}</p>
            ))
          }
        </div>

        <div>
          <h1 className='text-black text-lg font-montserrat font-semibold mb-5'>Contact Info</h1>
          {
            contactInfo.map((info) =>(
              <p className='text-slate-gray text-lg font-montserrat font-semibold mt-1'>{info}</p>
            ))
          }
        </div>
      </div>
      <div className='flex justify-between mt-16'>
          <p className='text-xs text-gray-400'>© Avi Bandi. All rights reserved</p>
          <div>
          <p className='text-xs text-gray-400'>© Avi Bandi. All rights reserved</p>

          </div>
      </div>
    </div>
  )
}

export default Footer
