import React from 'react'
// import Button from '../components/Button'
// import sheCan from '../images/sheCan.jpeg';
const Hero = () => {
  const statistics = [
    { value: '250+', label: 'Workshops' },
    { value: '3000+', label: 'Attendees' },
    { value: '9+', label: 'Countries' },
  ]
  return (
    <section
      id='home'
      className='w-full flex xl:flex-row flex-col justify-center min-h-screen gap-10 max-container'
    >
      <div className='relative xl:w-2/5 flex flex-col justify-center items-start w-full  max-xl:padding-x pt-28'>

        <h1 className='mt-10 font-palanquin max-sm:text-[72px] max-sm:leading-[82px] font-bold'>
          <span className='xl:whitespace-nowrap relative z-10 pr-10 text-5xl'>
            Hello, I am
          </span>
          <br />
          <span className='inline-block mt-3 text-blue-600 text-8xl'>Avi Bandi</span>
        </h1>

        <p className='font-palanquin text-slate-gray font-bold text-5xl leading-4 mt-10 mb-14 sm:max-w-sm'>
          Programmer
        </p>
        <a href='#about'>
        {/* <Button label='About Us' /> */}
        </a>
        <div className='flex justify-start items-start flex-wrap w-full mt-20 gap-16'>
          {/* {statistics.map((stat, index) => (
            <div key={index}>
              <p className='text-4xl font-palanquin font-bold'>{stat.value}</p>
              <p className='leading-7 font-montserrat text-slate-gray'>
                {stat.label}
              </p>
            </div>
          ))} */}
        </div>
      </div>

     
      
      {/* <div className='relative flex-1 flex justify-center items-center xl:min-h-screen max-xl:py-40 bg-primary bg-hero bg-cover bg-center'>
        <img
          src={sheCan}
          alt='shoe colletion'
          width={610}
          height={502}
          className='object-contain relative z-10'
        />
      </div> */}
      
    </section>
  )
}

export default Hero;
